-- ----------------------------
-- Procedure structure for add_org_id_to_all_tables
-- ----------------------------
DROP PROCEDURE IF EXISTS `add_org_id_to_all_tables`;
delimiter ;;
CREATE PROCEDURE `add_org_id_to_all_tables`()
BEGIN
  DECLARE done INT DEFAULT FALSE;
  DECLARE table_name VARCHAR(255);
  DECLARE cur CURSOR FOR
    SELECT TABLE_NAME
    FROM INFORMATION_SCHEMA.TABLES
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_TYPE = 'BASE TABLE';
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

  OPEN cur;

  read_loop: LOOP
    FETCH cur INTO table_name;
    IF done THEN
      LEAVE read_loop;
    END IF;

    -- 检查是否已有 org_id 字段
    SET @check_sql = CONCAT(
      'SELECT COUNT(*) INTO @col_exists FROM INFORMATION_SCHEMA.COLUMNS ',
      'WHERE TABLE_SCHEMA = DATABASE() AND TABLE_NAME = \'', table_name, '\' AND COLUMN_NAME = \'org_id\''
    );
    PREPARE stmt_check FROM @check_sql;
    EXECUTE stmt_check;
    DEALLOCATE PREPARE stmt_check;

    -- 若不存在，则添加 org_id 字段
    IF @col_exists = 0 THEN
      SET @alter_sql = CONCAT(
        'ALTER TABLE `', table_name, '` ADD COLUMN `org_id` BIGINT NOT NULL COMMENT \'组织id\';'
      );
      PREPARE stmt_alter FROM @alter_sql;
      EXECUTE stmt_alter;
      DEALLOCATE PREPARE stmt_alter;
    END IF;
  END LOOP;

  CLOSE cur;
END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
